import React, { Component } from 'react';
import styles from './RespostaForm.module.css';
import respostasForumData from './data/respostasForumData.json';

class RespostaForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      resposta: '',
      username: '',
      usuarioAutenticado: false
    };
  }

  handleChange(event) {
    this.setState({ resposta: event.target.value });
  }

  handleUsernameChange(event) {
    this.setState({ username: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    const { resposta, username } = this.state;

    if (username) {
      // ...existing code...

      // Get the last id from the existing responses
      const lastId = respostasForumData.respostas[respostasForumData.respostas.length - 1].id;

      // Create a new response object
      const newResponse = {
        id: lastId + 1,
        resposta: resposta,
        autor: username,
        data: new Date().toISOString(),
        curtidas: 0
      };

      // Make an HTTP request to the API endpoint to update the JSON file
      fetch('/api/respostas', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(newResponse)
      })
        .then(response => response.json())
        .then(data => {
          // Assuming the API returns the updated responses, you can handle the response data as needed
          console.log('New response added:', data);

          // Call the adicionarResposta function passed as a prop
          this.props.adicionarResposta(data);
        })
        .catch(error => {
          console.error('Error updating responses:', error);
        });

      // ...existing code...
    }
  }

  render() {
    const { resposta, username, usuarioAutenticado } = this.state;

    return (
      <div>
        <h3>Enviar Resposta</h3>
        {usuarioAutenticado ? (
          <form onSubmit={this.handleSubmit.bind(this)}>
            <textarea
              className={styles.respostaInput}
              value={resposta}
              onChange={this.handleChange.bind(this)}
              placeholder="Digite sua resposta..."
            ></textarea>
            <br />
            <button className={styles.enviarButton} type="submit">
              Enviar
            </button>
          </form>
        ) : (
          <div>
            <form>
              <input
                type="text"
                value={username}
                onChange={this.handleUsernameChange.bind(this)}
                placeholder="Digite seu nome de usuário..."
                required
              />
              <button
                className={styles.autenticarButton}
                onClick={() => {
                  if (username) {
                    this.setState({ usuarioAutenticado: true });
                  }
                }}
              >
                Autenticar
              </button>
            </form>
          </div>
        )}
        {usuarioAutenticado && (
          <div className={styles.usuarioLogado}>
            Usuário Logado: <span className={styles.username}>{username}</span>
          </div>
        )}
      </div>
    );
  }
}

export default RespostaForm;
