import React from 'react';
import styles from './TopicoPrincipal.module.css';
import forumData from './data/topicoPrincipal.json';

const TopicoPrincipal = () => {
  const { titulo, subtitulo, descricao, autor, dataPublicacao, curtidas, respostas } = forumData;

  return (
    <div className={styles.topicContainer}>
      <h1 className={styles.topicTitle}>{titulo}</h1>
      <h2 className={styles.topicSubtitle}>{subtitulo}</h2>
      <p className={styles.topicDescription}>{descricao}</p>
      <div className={styles.topicInfo}>
        <span className={styles.author}>Autor: {autor}</span>
        <span className={styles.date}>Data de Publicação: {dataPublicacao}</span>
      </div>
      <div className={styles.topicStats}>
        <span className={styles.likes}>Curtidas: {curtidas}</span>
        <span className={styles.replies}>Respostas: {respostas}</span>
      </div>
    </div>
  );
};

export default TopicoPrincipal;
