import React from 'react';
import forumTopicData from '../data/forumTopicData.json';
import styles from './ForumTopic.module.css';

class ForumTopic extends React.Component {
  render() {
    const { title, subtitle, description, author, date, likes, replies } = forumTopicData;

    return (
      <div className={styles.topicContainer}>
        <h1 className={styles.title}>{title}</h1>
        <h2 className={styles.subtitle}>{subtitle}</h2>
        <p className={styles.description}>{description}</p>
        <div className={styles.details}>
          <span className={styles.author}>Publicado por: {author}</span>
          <span className={styles.date}>{date}</span>
        </div>
        <div className={styles.stats}>
          <span className={styles.likes}>{likes} curtidas</span>
          <span className={styles.replies}>{replies} respostas</span>
        </div>
      </div>
    );
  }
}

export default ForumTopic;
