import React, { Component } from 'react';
import styles from './RespostaForm.module.css';

class RespostaForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      resposta: '',
      usuarioAutenticado: false, // Indicador de usuário autenticado (pode ser true ou false)
      username: '',
    };
  }

  handleChange(event) {
    this.setState({ resposta: event.target.value });
  }

  handleUsernameChange(event) {
    this.setState({ username: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    const { resposta, username } = this.state;

    // Verifica se o usuário está autenticado e se a resposta não está vazia
    if (username && resposta) {
      // Cria um novo objeto de resposta
      const novaResposta = {
        id: Date.now(),
        resposta,
        autor: username,
        data: new Date().toLocaleDateString(),
        curtidas: 0,
      };

      // Chama a função de adicionar resposta passada como prop
      this.props.adicionarResposta(novaResposta);

      // Limpa o estado do formulário
      this.setState({ resposta: '', username: '' });
    }
  }

  render() {
    const { resposta, usuarioAutenticado, username } = this.state;

    return (
      <div className={styles.respostaContainer}>
        <h3>Enviar Resposta</h3>
        {usuarioAutenticado ? (
          <form onSubmit={this.handleSubmit.bind(this)}>
            <textarea
              className={styles.respostaInput}
              value={resposta}
              onChange={this.handleChange.bind(this)}
              placeholder="Digite sua resposta..."
            ></textarea>
            <div className={styles.inputContainer}>
              <input
                className={styles.usernameInput}
                type="text"
                value={username}
                onChange={this.handleUsernameChange.bind(this)}
                placeholder="Seu nome de usuário"
              />
              <button className={styles.enviarButton} type="submit">Enviar</button>
            </div>
          </form>
        ) : (
          <p className={styles.autenticacaoMensagem}>Você precisa estar autenticado para enviar uma resposta.</p>
        )}
      </div>
    );
  }
}

export default RespostaForm;
