import React from 'react';
import respostasForumData from './data/respostasForumData.json';
import styles from './RespostasForum.module.css';

class RespostasForum extends React.Component {
  handleSubmit(event) {
    event.preventDefault();
    const { resposta, username } = this.state;

    if (username) {
      // ...existing code...

      // Get the last id from the existing responses
      const lastId = respostasForumData.respostas[respostasForumData.respostas.length - 1].id;

      // Create a new response object
      const newResponse = {
        id: lastId + 1,
        resposta: resposta,
        autor: username,
        data: new Date().toISOString(),
        curtidas: 0
      };

      this.props.adicionarResposta(newResponse);

    }
  }

  render() {
    const { respostas } = respostasForumData;

    return (
      <div>
        <h2>Respostas</h2>
        {respostas.map((resposta) => (
          <div key={resposta.id} className={styles.respostaContainer}>
            <p className={styles.resposta}>{resposta.resposta}</p>
            <div className={styles.details}>
              <span className={styles.autor}>{resposta.autor}</span>
              <span className={styles.data}>{resposta.data}</span>
            </div>
            <div className={styles.stats}>
              <span className={styles.curtidas}>{resposta.curtidas} curtidas</span>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default RespostasForum;
