import React, { Component } from 'react';
import styles from './RespostaForm.module.css';
import respostasForumData from './data/respostasForumData.json';

class RespostaForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      resposta: '',
      username: '',
      usuarioAutenticado: false
    };
  }

  handleChange(event) {
    this.setState({ resposta: event.target.value });
  }

  handleUsernameChange(event) {
    this.setState({ username: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    const { resposta, username } = this.state;

    if (username) {
      // Verificar se o nome de usuário foi inserido
      // Você pode adicionar sua lógica de autenticação aqui, como verificar se o nome de usuário existe em um banco de dados

      // Realizar a ação de envio da resposta
      console.log(`Usuário: ${username}`);
      console.log(`Resposta: ${resposta}`);

      this.setState({ resposta: '' });

      // Get the last id from the existing responses
      const lastId = respostasForumData.respostas[respostasForumData.respostas.length - 1].id;

      // Create a new response object
      const newResponse = {
        id: lastId + 1,
        resposta: resposta,
        autor: username,
        data: new Date().toISOString(),
        curtidas: 0
      };

      // Add the new response object to the array
      respostasForumData.respostas.push(newResponse);

      // Update the JSON file (assuming you are using Node.js fs module)
      const fs = require('fs');
      fs.writeFile('./data/respostasForumData.json', JSON.stringify(respostasForumData), err => {
        if (err) {
          console.error('Error writing JSON file:', err);
        } else {
          console.log('JSON file updated successfully.');
        }
      });

      // Call the adicionarResposta function passed as a prop
      this.props.adicionarResposta(newResponse);
    }
  }

  render() {
    const { resposta, username, usuarioAutenticado } = this.state;

    return (
      <div>
        <h3>Enviar Resposta</h3>
        {usuarioAutenticado ? (
          <form onSubmit={this.handleSubmit.bind(this)}>
            <textarea
              className={styles.respostaInput}
              value={resposta}
              onChange={this.handleChange.bind(this)}
              placeholder="Digite sua resposta..."
            ></textarea>
            <br />
            <button className={styles.enviarButton} type="submit">
              Enviar
            </button>
          </form>
        ) : (
          <div>
            <form>
              <input
                type="text"
                value={username}
                onChange={this.handleUsernameChange.bind(this)}
                placeholder="Digite seu nome de usuário..."
                required
              />
              <button
                className={styles.autenticarButton}
                onClick={() => {
                  if (username) {
                    this.setState({ usuarioAutenticado: true });
                  }
                }}
              >
                Autenticar
              </button>
            </form>
          </div>
        )}
        {usuarioAutenticado && (
          <div className={styles.usuarioLogado}>
            Usuário Logado: <span className={styles.username}>{username}</span>
          </div>
        )}
      </div>
    );
  }
}

export default RespostaForm;
